mapper.insert:
	INSERT 
	  INTO WORKOUT 
	     (
           EX_NO
         , USER_ID
         , WORKOUT_TITLE
         , WORKOUT_DATE
         , WORKOUT_CATEGORY
         , DIFFICULTY
         , DESCRIPTION
         , WORKOUT_STATUS
         ) 
	VALUES 
	     (
    	   SEQ_WORKOUT_EX_NO.NEXTVAL
    	 , #{userId}
    	 , #{workoutTitle}
    	 , TO_DATE (#{workoutDate}, 'YYYY-MM-DD')
    	 , #{workoutCategory}
    	 , #{difficulty}
    	 , #{description}
    	 , 'Y'
		)
